void Screen2ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &button1)
    {
        //Interaction1
        //When button1 clicked change screen to Screen1
        //Go to Screen1 with screen transition towards West
        application().gotoScreen1ScreenSlideTransitionWest();
    }
    else if (&src == &button2)
    {
        //Interaction3
        //When button2 clicked change screen to Screen3
        //Go to Screen3 with no screen transition
        application().gotoScreen3ScreenNoTransition();
    }
    else if (&src == &repeatButton1)
    {
        //Interaction5
        //When repeatButton1 clicked call virtual function
        //Call increaseVCO
        increaseVCO();
    }
    else if (&src == &repeatButton2)
    {
        //Interaction4
        //When repeatButton2 clicked call virtual function
        //Call decreaseVCO
        decreaseVCO();
    }
    else if (&src == &toggleButton1)
    {
        //Interaction6
        //When toggleButton1 clicked call virtual function
        //Call isTransmitting
        isTransmitting();
    }
}

void Screen2ViewBase::sliderValueChangedCallbackHandler(const touchgfx::Slider& src, int value)
{
    if (&src == &slider1)
    {
        //Interaction2
        //When slider1 value changed call virtual function
        //Call setValue
        setValue(value);
    }
}
